import React, {useEffect, useState} from 'react'
import {Form, Button, Col, Row ,ListGroup, Image, Card} from 'react-bootstrap'
import {useDispatch, useSelector} from 'react-redux'
import FormContainer from '../components/formcontainer'
import Message from '../components/Message'
import {Link} from 'react-router-dom'
import {getOrder, payOrder, deliverOrder} from '../store/actions/ordercreate'
import Loader  from '../components/Loader'
import axios from 'axios'
import {PayPalButton} from 'react-paypal-button-v2'
import {ORDER_PAY_REST, ORDER_DELIEVER_REST} from '../store/actions/actionTypes'


const OrderScrean = ({match}) => {

    const orderId = match.params.id

    const [sdkReady, setSdkReady] = useState(false)

    const dispatch = useDispatch()

    const addDecimals = (num) => {
        return (Math.round(num *100) / 100).toFixed(2)
    }



    const cart = useSelector(state => state.cart)



    const getCreate = useSelector(state => state.ordercreate)

    const {order, loading, error} = getCreate

    const orderPay = useSelector(state => state.ordercreate)
    const {success, loading: loadingPay} = orderPay

    const orderdeliver = useSelector(state => state.ordercreate)
    const {success:successDeliever, loading: loadingdeliver} = orderdeliver

    cart.itemsPrice = cart.cartItems.reduce( (acc, item) => acc + item.price * item.qty, 0 )


    useEffect( () => {

        const addPayPalScript = async () => {
            const {data: clientId} = await axios.get('/api/config/paypal')
            const script = document.createElement('script')
            script.type = 'text/javascript'
            script.src = `https://www.paypal.com/sdk/js?client-id=${clientId}`
            script.async = true
            script.onload = () => {
                setSdkReady(true)
            }

            document.body.appendChild(script)

        }


        if(!order || success || successDeliever ) {
            dispatch({type: ORDER_PAY_REST })
            dispatch(getOrder(orderId))
        } else if(!order.isPaid) {
            if(!window.paypal) {
                addPayPalScript()
            }else {
                setSdkReady(true) 
            }
        }

       // dispatch(getOrder(orderId))
    }, [dispatch, orderId, successDeliever ,success])


    const successPaymentHandler = (paymentResult) => {


        dispatch(payOrder(orderId, paymentResult))
        console.log(paymentResult)
    }

    return loading ? <Loader /> : error ? <Message variant="danger">{error}</Message>
    : <React.Fragment>
        <h1>Order {order._id}</h1>
        <div>

            <Row>
                <Col md={8}>
                    <ListGroup variant="flush">
                        <ListGroup.Item>
                            <h2>Shipping</h2>
                            <strong>Name: </strong> {order.user.name}
                            <a href={`mailto:${order.user.email}`}>{order.user.email}</a>
                            <p>
                                <strong>Address</strong>
                             {' '}   {order.shippingAddress.address}, {order.shippingAddress.city}, {' '}
                                {order.shippingAddress.postelCode}, {' '}
                                {order.shippingAddress.country}
                            </p>
                            {order.isDelivered ? <Message variant='success'>Delivered </Message>
                            : <Message variant='danger'> Not Delivered  </Message>

                             }
                        </ListGroup.Item>

                        <ListGroup.Item>
                            <h2>Payment</h2>

                            <p>
                                <strong>Method: </strong>
                             {order.paymentMethod}
                            </p>
                            {order.isPaid ? <Message variant='success'>Paid </Message>
                            : <Message variant='danger'> Not Paid </Message>

                             }
                        </ListGroup.Item>

                        <ListGroup.Item>

                        <h2>Order Items</h2>
                        {order.orderItems.length === 0 ? <Message>Your order is Empty</Message>
                         : (
                             <ListGroup.Item>

                                 {order.orderItems.map((item, index) => {
                                     return (
                                        <ListGroup.Item key={index} >
                                            <Row>
                                                <Col md={1}>
                                                    <Image src={item.image} alt={item.name}
                                                    fluid rounded  />

                                                </Col>
                                                <Col>
                                                <Link to={`/product/${item.product}`}>{item.name}</Link>

                                                </Col>


                                                <Col md={4}>
                                                    {item.qty} X ${item.price} = ${item.qty * item.price}
                                                </Col>

                                            </Row>
                                         </ListGroup.Item>
                                     )
                                 })}

                            </ListGroup.Item>
                         )
                          }

                        </ListGroup.Item>


                    </ListGroup>
                </Col>

                <Col md={4}>
                    <Card>
                        <ListGroup variant="flush">
                            <ListGroup.Item>
                                    <h2>Order Summary</h2>
                                </ListGroup.Item>

                            <ListGroup.Item>
                                <Row>
                                    <Col>Items</Col>
                                    <Col>${cart.itemsPrice}</Col>
                                </Row>
                            </ListGroup.Item>


                            <ListGroup.Item>
                                <Row>
                                    <Col>shipping</Col>
                                    <Col>${order.shippingPrice}</Col>
                                </Row>
                            </ListGroup.Item>

                            <ListGroup.Item>
                                <Row>
                                    <Col>Tax</Col>
                                    <Col>${order.taxPrice}</Col>
                                </Row>
                            </ListGroup.Item>

                            <ListGroup.Item>
                                <Row>
                                    <Col>Total</Col>
                                    <Col>${order.totalPrice}</Col>
                                </Row>
                            </ListGroup.Item>

                            {!order.isPaid && (
                                <ListGroup.Item>
                                    {/* {loadingPay && <Loader />}
                                    {!sdkReady ?  <Loader />: ( */}
                                        <PayPalButton
                                         amount={order.totalPrice}
                                         onSuccess={successPaymentHandler}
                                         />
                                    {/* )} */}
                                </ListGroup.Item>
                            )}

                            <ListGroup.Item>
                                {/* {error && <Message variant="danger">{order}</Message>} */}
                            </ListGroup.Item>



                        </ListGroup>
                    </Card>
                </Col>

            </Row>

        </div>
    </React.Fragment>
}

export default OrderScrean
